package CH09;

public class CH09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//메서드 호출
		printHello();
	}
	
	//메서드 선언
	//static : 프로그램 시작과 동시에 메모리에 올라간다.
	//void : 반환할 것이 없다.
	static void printHello() {
		
		System.out.println("만나서 반갑습니다.");
		
	}

}
